import React, { useState } from 'react';
import { Card, Button, Container, Form, Row, Col } from 'react-bootstrap';

const Feed = () => {
  const [posts, setPosts] = useState([
    { id: 1, user: 'Alice', content: 'Just finished my React project!' },
    { id: 2, user: 'Bob', content: 'Anyone interested in Django?' },
  ]);

  const [newPost, setNewPost] = useState('');

  const handlePost = (e) => {
    e.preventDefault();
    if (newPost.trim()) {
      setPosts([...posts, { id: posts.length + 1, user: 'You', content: newPost }]);
      setNewPost('');
    }
  };

  return (
    <Container className="my-4">
      <Card className="mb-4">
        <Card.Body>
          <Form onSubmit={handlePost}>
            <Form.Group controlId="newPost">
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="What's on your mind?"
                value={newPost}
                onChange={(e) => setNewPost(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit" className="mt-2">
              Post
            </Button>
          </Form>
        </Card.Body>
      </Card>
      <Row>
        {posts.map(post => (
          <Col key={post.id} md={6} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{post.user}</Card.Title>
                <Card.Text>{post.content}</Card.Text>
                <Button variant="success">Like</Button>
                <Button variant="info" className="ms-2">Comment</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Feed;
