import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Button, Container, Row, Col, Form } from 'react-bootstrap';

const Profile = () => {
  const [profiles, setProfiles] = useState([]);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    bio: '',
    skills: '',
    interests: ''
  });

  // Fetch profiles
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/profiles/')
      .then(res => setProfiles(res.data))
      .catch(err => console.error(err));
  }, []);

  // Update profile
  const handleUpdate = (id) => {
    axios.patch(`http://127.0.0.1:8000/api/profiles/${id}/`, formData)
      .then(res => {
        setProfiles(profiles.map(p => p.id === id ? res.data : p));
        setEditing(false);
      })
      .catch(err => console.error(err));
  };

  return (
    <Container>
      <h1 className="my-4">Profiles</h1>
      <Row>
        {profiles.map(profile => (
          <Col key={profile.id} md={4} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{profile.user.username}</Card.Title>
                {editing ? (
                  <Form>
                    <Form.Group className="mb-3">
                      <Form.Control
                        as="textarea"
                        rows={2}
                        placeholder="Bio"
                        value={formData.bio}
                        onChange={(e) => setFormData({...formData, bio: e.target.value})}
                      />
                    </Form.Group>
                    <Button variant="primary" onClick={() => handleUpdate(profile.id)}>Save</Button>
                  </Form>
                ) : (
                  <>
                    <Card.Text>{profile.bio}</Card.Text>
                    <Button variant="secondary" onClick={() => {
                      setEditing(true);
                      setFormData({
                        bio: profile.bio,
                        skills: profile.skills,
                        interests: profile.interests
                      });
                    }}>Edit</Button>
                  </>
                )}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Profile;
