import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, ListGroup, Form, Button, Container } from 'react-bootstrap';

const Messages = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Fetch messages
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/messages/')
      .then(res => setMessages(res.data))
      .catch(err => console.error(err));
  }, []);

  // Send a new message
  const handleSend = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:8000/api/messages/', {
      content: newMessage,
      receiver: 2  // Hardcoded for demo (replace with actual receiver ID)
    })
      .then(res => {
        setMessages([...messages, res.data]);
        setNewMessage('');
      })
      .catch(err => console.error(err));
  };

  return (
    <Container>
      <h1 className="my-4">Messages</h1>
      <Card>
        <Card.Body>
          <ListGroup>
            {messages.map(msg => (
              <ListGroup.Item key={msg.id}>
                <strong>{msg.sender.username}:</strong> {msg.content}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card.Body>
      </Card>

      <Form onSubmit={handleSend} className="mt-3">
        <Form.Group>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Type your message..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit" className="mt-2">Send</Button>
      </Form>
    </Container>
  );
};

export default Messages;
