import React, { useState } from 'react';
import { Card, ListGroup, Form, Button, Container } from 'react-bootstrap';

const Messages = () => {
  const [conversations, setConversations] = useState([
    { id: 1, user: 'Alice', lastMessage: 'Hey, how are you?' },
    { id: 2, user: 'Bob', lastMessage: 'Did you finish the project?' },
    { id: 3, user: 'Charlie', lastMessage: 'Let’s meet tomorrow!' },
  ]);

  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      alert(`Message sent: ${newMessage}`);
      setNewMessage('');
    }
  };

  return (
    <Container className="my-4">
      <h1>Messages</h1>
      <Card>
        <Card.Body>
          <ListGroup>
            {conversations.map(conversation => (
              <ListGroup.Item key={conversation.id} action>
                <strong>{conversation.user}</strong>: {conversation.lastMessage}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card.Body>
      </Card>

      <Card className="mt-4">
        <Card.Body>
          <Form onSubmit={handleSendMessage}>
            <Form.Group controlId="newMessage">
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Type your message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit" className="mt-2">
              Send
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Messages;
